sp_widget.client_script:
api.controller=function($scope, $rootScope) {

	// Enhancement: Scrollable / overflow navigation menu

	/* widget controller */
	var c = this;
	//$scope.tables = data.tables;
	$scope.loading = false;
	$scope.results = {};
	$scope.search = "";
	$scope.hits = 0;
	$scope.table = "All Tables"
	$scope.table_name = ""


	$rootScope.$on('searchResults', function(event, searchObj) {

		$scope.loading = searchObj.scope.loading;
		$scope.search = searchObj.scope.search;
		$scope.hits = searchObj.scope.hits;

		$scope.data = searchObj.scope.data;
		$scope.results = searchObj.scope.results;

		$scope.table = searchObj.scope.table;
		$scope.table_name = searchObj.scope.table_name;

	});
};

sp_widget.css:
.navigation-menu {
  position:fixed;
}

