sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	if (g_form.getValue("applicable_in_context") == "client" || 
		g_form.getValue("applicable_in_context") == "client_server") {
		evaluateSample();
	}
}



function evaluateSample() {

	// Parse keys and values out of "sample input" JSON field
	var jsonObject = g_form.getValue("sample_input");
	var parsedObject = JSON.parse(jsonObject);
	var evalString = "";

	var keys = Object.keys(parsedObject);
	for (var i = 0; i < keys.length; i++) {

		var key = keys[i];
		var value = parsedObject[key];

		// Compose eval string with JSON variables declared as regular JS variables
		evalString += 'var ' + key + ' = "' + value + '"; ';
	}

	// Add client script to eval string
	evalString += g_form.getValue("client_script");

	// Execute eval string and place in "sample output client" field
	g_form.setValue("sample_output_client", eval(evalString));
}
