sys_script_client.script:
function onLoad() {

	if (g_form.getValue("applicable_in_context") == "client_server") {
		g_form.showFieldMsg("client_script", "To be applicable in Client/Server Context, Client script should mirror the code of the Server script.  To test, evaluate that the sample input produces the same output when executed both with the Client script and with the Server script.");
		g_form.showFieldMsg("server_script", "To be applicable in Client/Server Context, Server script should mirror the code of the Client script.  To test, evaluate that the sample input produces the same output when executed both with the Client script and with the Server script.");
	}
}
