function runScheduledScriptJob(scriptSysID){
	//Execute a scheduled script job  
	// Execute a scheduled script job  
	var rec = new GlideRecord('sysauto_script');  
	rec.get('sys_id', scriptSysID); // Sys_ID of Scheduled Job being executed.  Because names of scheduled jobs often change, it is preferable to use the sys_id  

	gs.executeNow(rec);  


}
